(window.webpackJsonp=window.webpackJsonp||[]).push([[131],{605:function(v,_,p){"use strict";p.r(_);var t=p(2),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("药谷南面是山崖，当年何无漪初次带她出谷便走的那条山路。如今回去她们得绕老大一圈从东面进去，东面离大城不远大路畅通，平常出入都是往那条路上走的，此事何澜生六岁时回去才清楚，而药谷不过是隐在太行群山之中的一片山谷，三面环山，也就无漪那样的本事敢往南面的山崖走。")]),v._v(" "),_("p",[v._v("眼前路像是一刀切开山体留下的缝隙，两边山崖垂直而下，远看着山几乎连成了一体，路几近于无，可继续往前走会发现路还是那么宽。何澜生起初猜测也许是人眼睛的问题，走了几次也就见怪不怪了。走过这一段还有好几个弯，就是在群山之中打转。回来的少，路线她至今也没记住。")]),v._v(" "),_("p",[v._v("眼见快到了正午，何澜生还在考虑能否赶上午饭，药谷的大门就到了。所谓大门，不过是搭了个草棚，估计有外人过来不往里走一会也会以为是荒无人烟的地方。不远处一人就小跑过来，直接奔到了何无漪的马下，蹦蹦跳跳地说：“都回来了！谷主，少谷主！”小女孩七八岁的模样，梳着两羊角辫，碎花小裙穿着别样可爱。")]),v._v(" "),_("p",[v._v("孩子何澜生认识，是念心她三叔的小女儿，名唤何念安，乳名小安平。她大姐何念思大上她三岁，早在她还未出生时就选了作她的贴身侍女。何澜生虽说不喜欢人伺候，但念心得要个接班的，就没推辞。")]),v._v(" "),_("p",[v._v("何澜生笑着下马来，捏了把她红扑扑的小脸说：“一眨眼就长大了，小安平想不想我？”念安就咧嘴笑着说：“想！”笑起来还缺了两颗门牙，可爱的模样把何澜生逗笑了。")]),v._v(" "),_("p",[v._v("“你大姐在后头，去看看吧。”何无漪摸了摸她的羊角辫，她便高兴地蹦蹦跳跳往后面去了，大声喊着：“大姐！夏姐姐！丘姨！欸……还有”")]),v._v(" "),_("p",[v._v("念心应了，笑着给她指人：“小安平，喊苏姐姐，后面的是胡大叔，胡二叔，胡三叔，胡四叔。”")]),v._v(" "),_("p",[v._v("“嗯！”小女孩点头哈腰的都喊了，憨头憨脑的小模样逗乐了众人，独独丘沙冷着张脸。")]),v._v(" "),_("p",[v._v("念心都三十三了，夏雨更是到了三十四，而丘沙不过二十六，何澜生幸灾乐祸地问：“小安平为何喊丘沙叫姨呢？”立马就得了丘沙一记冷眼。念安看了看丘沙就缩到何无漪的身后去了，道：“…丘姨生气了，少谷主她欺负我。”")]),v._v(" "),_("p",[v._v("念心大笑道：“哈哈，小安平，去谷主的怀里，少谷主她就不敢欺负你了。”众人看着何澜生一脸奈何不得的神情也跟着哄笑起来，何澜生被笑的挂不住脸就要去无漪背后捉念安。小孩子灵活得很，在无漪身后左躲右躲没给她抓到，还有空朝她吐舌头。其他人看着又是哄笑一通，何澜生是彻底窘透了脸。")]),v._v(" "),_("p",[v._v("何无漪就笑着把她拉进了怀里：“多大了，还闹。”念安给她做了个鬼脸奔向了念心的怀里，笑得一脸灿烂。")]),v._v(" "),_("p",[v._v("何澜生红着脸说：“是她们起哄。”抬头见无漪含笑不语更是不好意思，羞臊地喊：“无漪！”")]),v._v(" "),_("p",[v._v("见她这模样，何无漪就道：“好了，大家也快些回去。”其他人笑着打马走了，何无漪才捏着她的耳朵道：“澜儿最近越发傻气了，嗯？”")]),v._v(" "),_("p",[v._v("“哪有，不许说我傻了。”何澜生恼得很，瞪了她一眼也不要何无漪抱了，“回家，你也总爱戏弄我。”说完拉着何无漪大步往前走，何无漪笑着跟在她后面，小云没事就一溜烟跑了。何澜生瞪大眼睛看着马屁股越来越远，指着前路道：“无漪，小云走了？”")]),v._v(" "),_("p",[v._v("“走了啊。”何无漪笑着说，完全不在意离家还有老长的路。何澜生顿觉腿软，脚下一个踉跄差点摔了。看着何无漪没事似的继续往前走，她只有拍了拍裤腿跟上去，心里老觉得又被欺负了。")]),v._v(" "),_("p",[v._v("两人入了村口，就被东家说一句去家里吃饭，西家塞上把小点心……如此耽搁了好久，等念心回头寻过来才脱了身，何澜生身上手里塞满了各种东西，鼓鼓囊囊的都走不动路了。她哀怨地瞅着两袖清风的无漪，嘀咕着病好了就不待见她了。刚说完无漪就回过头来，何澜生瞪回去她就笑了，吃定了何澜生拿她没办法。")]),v._v(" "),_("p",[v._v("费了好大的力气爬到山腰上的家里，何澜生累得直喘气。好歹进屋何无漪帮了把手，把她手里的一包点心给提过去了。何澜生还没说句话点心就进了她的嘴里，无奈地转过头欲哭无泪，那是她最爱的黄豆糕啊。不高兴的放下一堆东西，何澜生自个去了里屋。")]),v._v(" "),_("p",[v._v("刚踏进屋里就见一女子背对着她，何澜生见背影陌生奇怪地问：“你是？”女子听到声音突然转过来，头上的朱钗叮当作响，见了何澜生就紧张地说不出话来：“少谷主回来了，我，我来看看还缺了什么……”")]),v._v(" "),_("p",[v._v("“少谷主的伤势如何了？”")]),v._v(" "),_("p",[v._v("和念心有些相似的长相，是念思无疑。两年没见她变化她挺大，以前的丫头片子都长开了。何澜生笑着说：“没事了。念思啊，我一时还认不出你了，记得当初你可是哭着要跟我们出门呢。”")]),v._v(" "),_("p",[v._v("念思紧张地更是说不出话来：“少，少谷主，我那时不懂事……”说得像是要哭了，何澜生忙笑道：“好了，这不懂事了么。” 念思才堪堪止住了泪道：“大姐也说我该晓得事了。”")]),v._v(" "),_("p",[v._v("何澜生点头，相比念心而言她已经算是晚了不少，再过几年学就迟了。“念心刚回来事情多，你去帮帮吧。那些琐事理顺了来做就好。”")]),v._v(" "),_("p",[v._v("“唉，少谷主您歇歇。”念思泡了杯茶给她就走了。何澜生喝了口坐在床榻上松了口气，还是药谷待着轻松。她伸了个懒腰靠在床头，走路走得腿脚酸乏还真困了。可身上脏她又睡不下，打了个哈欠还想起身去厨房看看有热水没，结果迷迷糊糊就睡过去了。")]),v._v(" "),_("p",[v._v("“澜儿？”何无漪推门进来喊了声没人应，快步走到床边见何澜生睡过去了伸手就摸上了她的脉搏，许是动作太大反而惊醒了她，何无漪就改为抓着她的手，刮了刮她的鼻尖说：“怎么睡了，很困？”")]),v._v(" "),_("p",[v._v("何澜生打了个哈欠泪眼朦胧地点头抱怨：“还不是你让我走了那么久。”")]),v._v(" "),_("p",[v._v("“你病了许久要多走走，不然身子虚。”何无漪抱起她拉开被子，“那睡一会，醒了娘提水过来给你洗洗？”")]),v._v(" "),_("p",[v._v("“别，身上脏呢，回头床单念心她们又要洗一遍。我靠着你一会就好。”何澜生倒在她的怀里蹭了蹭，没睡着就嘀嘀咕咕地说，“无漪你个小气鬼，不就说了句么，让我走了这么久还吃了我的黄豆糕。”")]),v._v(" "),_("p",[v._v("“娘给你留了，哪能全吃了。”何无漪低头亲了亲她，“睡不着了？”")]),v._v(" "),_("p",[v._v("亲的有些痒何澜生就躲，几番动作那点睡意都没了，只好睁开眼睛对着她说：“被你吵没了，怎么办？”")]),v._v(" "),_("p",[v._v("“再跟娘下趟山？”何无漪说完见何澜生一脸苦相笑意更甚，抱紧了她说：“睡不着就去洗洗，就会赖着娘。”")]),v._v(" "),_("p",[v._v("“你喜欢。”何澜生抬头亲了她一口，“是不是？”")]),v._v(" "),_("p",[v._v("“你这么重娘可不喜欢了。”何无漪摇头说。何澜生才不信，抬头还想亲她，没亲上外头就有人敲门了。何澜生只好止住，赖在何无漪的怀里也不撒手，闷闷地说：“什么事？”")]),v._v(" "),_("p",[v._v("“水烧好了，少谷主在屋里洗？”")]),v._v(" "),_("p",[v._v("“送进来吧。”何澜生不高兴的松开手，理了理弄乱的衣服，突然又觉得回药谷不太好了。何无漪笑着哄她：“娘和你一起洗？”")]),v._v(" "),_("p",[v._v("“不要。”何澜生果断拒绝了，一起洗还是她受罪，她都不知道多久没看过无漪的身子了。这么一想脸就红透了，暗自骂了自己一句没出息。何无漪没再说什么就出去了，弄得何澜生心里又空落落的。她又不是不知事的古代女子，可有些事她们之间没有比有更好些。")]),v._v(" "),_("p",[v._v("回来后无漪没再提要出谷的事情，何澜生多少清楚是在等明年的及笄礼。何澜生书房每天都堆了一堆的事，药铺的药谷的都往她这送来。何无漪本就不管药谷之事，夏雨又有交托给何澜生的意思，便寻了天凉爽的阴天和谷中的管事一起去了书房。")]),v._v(" "),_("p",[v._v("何澜生见他们的仗势就头大，她病了两个多月无漪没让她碰半点琐事，如今要事都堆在一起忙得她快晕了。放下笔半黑着脸说：“大家都坐吧，有事但说无妨。”")]),v._v(" "),_("p",[v._v("夏雨恭敬的一拜说：“此事我们也商量了许久，谷中之事历来由谷主亲手打理。当年老谷主亲手将药谷托付于我等，唯恐辜负所望，思虑再三觉得将谷里事务归还于少谷主为好。”")]),v._v(" "),_("p",[v._v("何澜生就知道这天还是得来，从去年就送了一堆名册杂事过来夏雨也是早早打定了心思的，可药铺还一大摊子事情呢。大为头疼地说：“无漪也是这意思？”")]),v._v(" "),_("p",[v._v("“谷主说由少谷主的意思来。夏雨多一句嘴，药谷迟早还是交给少谷主的。”旁人又催了一句：“少谷主还应尽早打算。”")]),v._v(" "),_("p",[v._v("摆明着说早一天晚一天还是得她干，何澜生叹了口气，见其他管事也都早生华发也不好勉强，毕竟谷里多数人过得无忧无虑，年年风调雨顺的没啥糟心事。想了下只得点头：“事情我应下了。但此事需从长计议，你们选些中意的后辈，教好给我瞧瞧，再来详谈，众位以为如何？”")]),v._v(" "),_("p",[v._v("“少谷主应了我们几个心里就有底了。当如少谷主所言从长计议。”谷中德高望重的刘老摸着花白的胡须说，见何澜生脸色不好就笑着道：“那少谷主忙了，我等就不叨扰了。”")]),v._v(" "),_("p",[v._v("敢情就是来探她的底，怎么就这么容易应了呢，何澜生闷气不已，好歹没忘了正事：“夏雨我之前与你提的人呢？对了，你带念思多出门历练历练，老关在谷里也不是个事。”")]),v._v(" "),_("p",[v._v("“是。我将他们下派到各地的药铺去了，现在还在路上。”夏雨笑着说，“今个少谷主应了，是给大家定了心。药谷出不得第二个谷主，时久必乱。”")]),v._v(" "),_("p",[v._v("何澜生不以为然，无漪做事可都有底呢，哪里真的是不管不顾的人，可……她不喜欢也是真的。一大群的人总的有人看着，何况是堆治病救人的大夫。何澜生犹豫下还是不确定地问：“夏雨，你与我说实话，我可真的扛得下这副担子？”")]),v._v(" "),_("p",[v._v("论医术她不行，论医德她恐怕也不及格，她心里清楚自己骨子里还是个商人，她曾祖父是救天下的仁医，无漪更是个彻头彻尾的医痴……她何德何能能做得起那个位子，当得起天下医者的领头羊。")]),v._v(" "),_("p",[v._v("夏雨道：“当初我听闻五岁幼童妄图开天下的药铺，视之如儿戏，如今过了九年一家药铺已遍布天下，而少谷主年不过十四，论之天下谁能如此！药谷交给少谷主是众望所归。”一通夸下来何澜生还是没缓一口气，隔行如隔山，商人和大夫明显不能按一个层次走，没料夏雨顿了下又说：“谷主在的一天，少谷主就不会踏错路。”")]),v._v(" "),_("p",[v._v("何澜生生生被说红了脸，胡乱地点了点头叫了夏雨下去。暗道他们一个个算盘打得真响，想好了有无漪压着哪里还能让她胡乱蹦跶，敢情她刚才担心半天完全是瞎操心，暗自生了几口闷气，想着往后必然要多叨扰他们才是。")]),v._v(" "),_("p",[v._v("正说着何无漪就敲了门进来，拉着何澜生就要出去玩。何澜生看着书桌上一堆事情，再看看无漪亮着光的眸子，哀叹道：“鱼和熊掌不可兼得，孟夫子您诚不欺我也……”")]),v._v(" "),_("p",[v._v("听得何无漪好笑不已，刮了刮她的鼻子转身一溜烟走了。何澜生追出门去哪里还能见到影子，跺了跺脚只好回书房继续做事，刚关了门就被人抱住了。“娘没走呢。”何无漪靠着她的肩膀问：“澜儿为何要答应？”")]),v._v(" "),_("p",[v._v("温热的气息拂过耳廓，何澜生偏了偏头说：“早晚都一样，早点准备还省得往后麻烦。再说他们忙碌了多年也该歇歇，念心和夏雨也是。”药谷的条件算是好的，可寿命还是不高，活到七十的都是少数，外头平均寿命能有五十岁就不错了。半响何澜生没听她答话，有些奇怪，何无漪却抱紧了她闷闷地问：“娘也老了？”")]),v._v(" "),_("p",[v._v("“没呢，无漪还是小孩子。”何澜生转过身说，“比我都年轻。”话说的是实话，可何无漪听得就不是了，低头就往脸蛋上啃了一口。何澜生摸着口水印哭笑不得，还说不是小孩子脾气。")]),v._v(" "),_("p",[v._v("“跟娘出去转转，嗯？”何无漪啄了她一口说，也不管澜生答应不答应抱着她就走了。何澜生就看着书房越来越远，无奈随她去了，不过无漪的力气大了好多，是练剑的缘故？她找了个舒服的位子靠着，难得偷得半日闲。")])])}),[],!1,null,null,null);_.default=n.exports}}]);