(window.webpackJsonp=window.webpackJsonp||[]).push([[137],{612:function(v,_,p){"use strict";p.r(_);var t=p(2),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("何无漪就那么直接的承认了，脸上挂着淡淡的笑意往何澜生看去。那种似玩闹又像认真的眼神，看得何澜生心里紧张不已，追着她的目光想问问。两人的视线撞在一块，何澜生就脸红起来，好似对着那双幽深的眼睛把心里的小心思都交待了。")]),v._v(" "),_("p",[v._v("“澜儿过来。”何无漪含笑地唤她，“来洗洗脸。”")]),v._v(" "),_("p",[v._v("“喂，他都欺负你了，你怎么还听他的？俺娘说欺负媳妇的汉子不是好汉子，你别去！”水娃子拉着何澜生的衣服，还不忘瞪了眼何无漪。何无漪得意地朝何澜生招手：“莫听他说，快过来。”")]),v._v(" "),_("p",[v._v("“无漪……”何澜生被两人闹得无语，还是听了何无漪的话走了过去。走近了何无漪抓了她的手一把把她抱进了怀里，对着水娃子挑眉道：“澜儿是我的，你牵你家的牛去。”")]),v._v(" "),_("p",[v._v("水娃子见何澜生倚在何无漪的怀里也是脸红，挠了挠后脑勺说：“去就去，俺还不管了。”边走边嘀咕，到底咋回事？我都糊涂了。反正他媳妇对他真好，都不生气的，要是俺娘得揍得俺爹下不来地。")]),v._v(" "),_("p",[v._v("小孩子不知道自己嘀嘀咕咕的话都被人听了去，牵了牛就往家赶，留下红了脸的两人。幸而是脸上抹了东西看得不太明显，何澜生羞恼地捏了下她的手心就蹲在小溪边洗脸，洗完了就见水面映着她的那张脸，脸上红晕遍布，看得何澜生自己都呆了。")]),v._v(" "),_("p",[v._v("何无漪在一旁洗干净了拿了手帕帮她擦拭，眼底隐隐流转着述不清的情愫，左手不知何时搂上了她的腰间，轻轻往怀里一带，何澜生的眸子就近在眼前，眸子里的欢喜与情意教何无漪舍不得移开眼，缠着她看了又看，看得何澜生那张脸似如火烧，才笑着去亲吻她：“澜儿，澜儿……”")]),v._v(" "),_("p",[v._v("一声又一声，好似千般话语都在这轻声低喃中。")]),v._v(" "),_("p",[v._v("丘沙赶着马车进了长溪村，沿着村旁的路走了好半天才就见到夕阳下相拥的两人，只看了眼便停了车，随小云在四处晃荡。何无漪听到了动静，在何澜生耳边说：“丘沙来了，随娘去蔡伯家看看。”")]),v._v(" "),_("p",[v._v("“哦。”何澜生恋恋不舍地离开她的怀里，偷偷瞧了她一眼，见她脸上淡淡的红晕就笑，伸手去握着她的手，手指往她的指缝间遛，何无漪抓住了她作乱的手指头，十指相扣在一起。何澜生便笑起来，一路挨着何无漪走到马车旁，心扑通扑通的跳地好快。")]),v._v(" "),_("p",[v._v("丘沙面无表情地下车来，和何无漪点了点头就走远了些。")]),v._v(" "),_("p",[v._v("“娘去车里换身衣服。”")]),v._v(" "),_("p",[v._v("“湛卢你拿好。”何澜生松开手把湛卢给她，转身去了丘沙身边，“后头有人追过来么？”")]),v._v(" "),_("p",[v._v("“没有，属下并未发现。”")]),v._v(" "),_("p",[v._v("“那就好。”何澜生点头，“路上要麻烦你注意些，都过了一年了八荒山的事情还没歇恐怕有些难办，不行就多调些人。”")]),v._v(" "),_("p",[v._v("“是。”")]),v._v(" "),_("p",[v._v("何澜生见丘沙面上没什么波动，欲言又止。正好何无漪掀了帘子出来，她脱了冠帽挽了个发髻，衣服没换只在袍子外面披了件女装，湛卢上了剑鞘依旧挂在她腰间，漆黑如墨沉静无声。")]),v._v(" "),_("p",[v._v("何澜生一晃眼也觉得湛卢剑挺适合她的，就咽回去想说的话上前帮她理了理衣服：“天快黑了，打算在蔡家暂住一宿？”")]),v._v(" "),_("p",[v._v("“能找到住的咱们就别麻烦人家了。”何无漪吹了声口哨将小云叫过来，三人沿着村路往蔡家去。好巧不巧，何无漪刚到蔡家门口就遇到方才的放牛娃，他好奇地盯着两人看，疑惑地说：“两位姐姐俺打哪见过？”")]),v._v(" "),_("p",[v._v("何无漪笑着说：“见过么，澜儿你说说？”她一开口水娃子就喊道：“是你这贼人！咦……你是女的？”")]),v._v(" "),_("p",[v._v("“水娃子在外面囔囔些什么，快过来吃饭！”屋里头的妇人出来喊他，见着何无漪忙大喜道：“何大夫！快进来吃饭。”妇人一身暗棕色粗布衣裳，上面斑斑点点的都是污迹，手里刚刷锅一手油腻腻的也不好意思伸过来，在衣服上擦了擦说：“俺爹盼着您今天给他看看呢，都一起进来坐坐。”往屋里头大喊了句：“老蔡你给老娘滚出来，何大夫来了。”")]),v._v(" "),_("p",[v._v("何澜生往四周看了圈，平常村里的小户，屋外用围栏围了圈，收拾的还算妥帖，和蓬头垢面的妇人相比好太多。再看水娃子虽然晒得黝黑黝黑的，身上的衣物完完整整的一点都不破，不得不说那妇人是个会持家的，就是不会打理自个。她随了无漪进屋，刚进去就从里屋出来一壮年男子，见了何无漪也笑着上前道：“何大夫快屋里坐，媳妇去加几个菜上来。”")]),v._v(" "),_("p",[v._v("“不麻烦了。我来给蔡伯看看。”何无漪推辞说，拉着何澜生就往旁的屋子去，“蔡伯睡了么？”")]),v._v(" "),_("p",[v._v("“刚吃了晚饭，没这么早睡。”男子敲了敲门：“爹，何大夫过来瞧您了，开开门。”")]),v._v(" "),_("p",[v._v("“什么？”")]),v._v(" "),_("p",[v._v("男子大了声喊：“昨个那位何大夫，给您瞧病来了。”")]),v._v(" "),_("p",[v._v("“欸，来了。”屋里头的老人应了声，半天才见门打开。屋子里黑乎乎的，想必是省灯油没点，何澜生只见一双瘦骨嶙峋的手在缓慢地往后拉门，待开了门才见得是个精神不错的老头子，见了无漪那眼神铮亮的。“何大夫您医术真是不赖，俺这条腿今天就能走个两圈了。”")]),v._v(" "),_("p",[v._v("“是咯是咯，俺爹一直在念叨您妙手回春呢。”蔡家妇人端着菜走过笑着说。当地的野菜也别有一番香味，引得何澜生肚子咕噜一声。妇人见何澜生的馋相脸上满是红光：“大姑娘饿了就过来吃，俺们不跟城里头那样讲究。”")]),v._v(" "),_("p",[v._v("水娃子插嘴道：“哪里是什么大姑娘，是她媳妇嘞。”")]),v._v(" "),_("p",[v._v("母子俩说得何澜生脸上火烧火燎的，恨不得找个地方埋了算了。何无漪笑了笑说：“澜儿饿了就留下来吃点。”也没反驳水娃子的话。蔡家妇人以为自家儿子说胡话，瞪了眼水娃子，瞪得水娃子委屈的瘪了瘪嘴。")]),v._v(" "),_("p",[v._v("“先给老人家看病。”说着嗔了眼笑着的何无漪，“你又闹我。”何无漪笑而不语，给蔡伯看腿。何澜生在一旁打下手，做得有模有样的，何无漪眼神时不时落在她身上，此间情意被蔡伯一双眼睛看得明明白白。")]),v._v(" "),_("p",[v._v("“蔡伯多走动些好的快，方子的药明日起可减两成抓。”何无漪说完收了针，掌间运气帮他疏通经脉，好一会才收了手。何澜生就端了水来给她洗手，何无漪洗了才问何澜生：“方才娘做的你可懂了？”")]),v._v(" "),_("p",[v._v("“懂了七八分。”何澜生点头，“晚上回去我与你细说。”")]),v._v(" "),_("p",[v._v("蔡伯听了两人说话闪过一丝惊讶，细看了两人叹了声才道：“留下吃了饭再说，俺这把老骨头还指望何大夫瞧好呢。”")]),v._v(" "),_("p",[v._v("“唉，爹您好好躺着。”男子应了便是如何也要留了何无漪吃了饭再走，何无漪也就留下了，顺带刮了刮何澜生的鼻子说了句：“贪吃货。”亲昵的让一旁瞧着她们的水娃子都红了脸，摸着脸颊直嘀咕怪事，俺脸红啥。")]),v._v(" "),_("p",[v._v("可怜丘沙等在屋后喝了半天的西北风，何澜生才记得给她包了点地瓜。丘沙接过地瓜冷眼瞥了她好几次反倒让何澜生心里大呼报应不爽，谁叫她之前把她易容的那么丑了。何无漪看着她俩眼刀子来来去去，拍了下何澜生的脑袋，叫她去拿了私藏的零嘴出来，何澜生苦了脸往包袱里掏了，掏出一大包东西。")]),v._v(" "),_("p",[v._v("干果蜜饯，饴糖糕点，大包小包林林总总的都有，在太原城三天可见是花足了钱买。何无漪一见她如此就扯她的脸颊道：“娘买了几件衣服，澜儿可就恼了啊？”")]),v._v(" "),_("p",[v._v("何澜生脸蛋被她扯变了形，呲牙咧嘴地说：“那些衣服你又不穿。”还买的又多，到底没敢说无漪胡乱花钱之类的，心里打定不能让她身上留一两银子。")]),v._v(" "),_("p",[v._v("丘沙坐在驾车的地方吃地瓜，对两人打情骂俏早已视若无睹。三人问了半天好歹寻到了附近的一处人家借住，蔡伯的病要多照料几天，她们就暂住了下来。")]),v._v(" "),_("p",[v._v("大清早的何澜生没事在溪边走走，早上的水汽重，溪边雾气蒙蒙的，偶有几朵野花，蔡家的水娃子牵着牛在喝水。")]),v._v(" "),_("p",[v._v("相处了几天，何澜生对他家还算熟悉，就问了句：“水娃子，这么早就放牛？”说了半天也没见那孩子回句话，牵了牛就走。何澜生纳闷，自己何时得罪他了？一会又见那孩子回过头来，气呼呼地说：“你不是何大夫的媳妇！你们俩一起骗俺！大骗子！”")]),v._v(" "),_("p",[v._v("“……”何澜生噎了半天，敢情这孩子死性子还记得那回事呢。迟疑一会还是叫住了要走的水娃子：“我何曾说我不是了？”")]),v._v(" "),_("p",[v._v("“俺娘说的，何大夫是女的，她还是你娘！”说着委屈得啪嗒啪嗒直掉眼泪：“俺娘还骂我胡说，揍了我一顿。”")]),v._v(" "),_("p",[v._v("“哪里疼了？”何澜生忙上前看了看，见他胳膊上有些红说：“我拿点药酒给你擦擦，你娘还真下手。牵着牛随我过来。”何澜生蹲下来看伤 ，凑得近水娃子也瞪大眼睛瞧她：“你真好看，比俺表姐还好看。人也好，俺以后也要娶你这样的媳妇。”")]),v._v(" "),_("p",[v._v("何澜生失笑，赏了他一个毛栗：“人小鬼大。”")]),v._v(" "),_("p",[v._v("“俺说真的！你是不是何大夫的媳妇？”说着仰着黑葡萄似的目光看着何澜生，何澜生摸着他脑袋问他：“你觉得呢？”")]),v._v(" "),_("p",[v._v("“俺……”小孩子挠了挠脑袋，“俺咋知道，俺娘说汉子才能娶媳妇。俺也不会娶俺娘啊，那是俺爹的媳妇！”")]),v._v(" "),_("p",[v._v("“噗……”何澜生被这实在孩子逗笑了，水娃子黝黑的小脸气鼓鼓地道：“你笑我！”")]),v._v(" "),_("p",[v._v("“我笑的不是你，笑的是世道。水娃子，是世人只容许汉子娶媳妇，不是汉子才能娶媳妇。”何澜生蹲下来小声道：“我只告诉你哟，我是何大夫的媳妇。世人不容许，你娘才会打你的。”")]),v._v(" "),_("p",[v._v("水娃子懵懵懂懂地问：“你娘不打你么？”")]),v._v(" "),_("p",[v._v("“我娘宠着我，舍不得打我。”何澜生笑着说，眉目间的柔情让水娃子看得有点呆，半懂不懂地点头，又问：“你娘不是何大夫？”")]),v._v(" "),_("p",[v._v("“是她呢。”")]),v._v(" "),_("p",[v._v("“呀！你抢了你爹的媳妇？！”")]),v._v(" "),_("p",[v._v("“……我只有我娘，我娘也只有我。”")]),v._v(" "),_("p",[v._v("“就你们两人，哦，所以你是她媳妇？”小孩子似乎被绕晕了，问得越发不靠谱，何澜生听了发笑，牵着他在小溪边慢悠悠地走。“水娃子大了有心上人就会晓得，心里有她一人的感觉，就是错了也错得心甘情愿。”")]),v._v(" "),_("p",[v._v("“心里是哪里？”水娃子摸了摸胸口，问道：“这里？能装的下那么大的人？”")]),v._v(" "),_("p",[v._v("“对啊，闭上眼睛能想到的人就装了进去。”何澜生柔声说着，心里敞开了底像是什么都能说出来似的，她笑了笑，或许水娃子还小不懂吧，好久好久都没找人说说呢。她叹了口气，身子就被抱进了熟悉的怀里，淡淡的药味传入鼻尖耳边是她那熟悉的嗓音：“澜儿。”如玉落盘中，温润动人。")]),v._v(" "),_("p",[v._v("何澜生便靠在她怀里，闭着眼睛问：“偷听了多少？”")]),v._v(" "),_("p",[v._v("“都听见了，澜儿没对娘说过呢。”")]),v._v(" "),_("p",[v._v("“你总逗弄我，说给你听作何。水娃子我带你去上药，莫要理她。”何澜生微红着脸，挣了出来。没料道水娃子从她眼皮子底下跑了出去说：“何大夫你媳妇可好了，你不要我要娶嘞。”说着跳上了牛背，赶着牛往回走。")]),v._v(" "),_("p",[v._v("“水娃子！”何澜生红了脸喊道，刚才还说这孩子实在怎么就调皮捣蛋起来，恨不得抓了回来，一想他胳膊上还有伤呢忙喊道：“你胳膊不上药啊？”")]),v._v(" "),_("p",[v._v("“俺去放牛了！”说着挥了挥手慢悠悠地骑着牛走了，何澜生想这么大的男孩子挨打也是家常便饭，只好作罢。")]),v._v(" "),_("p",[v._v("何无漪见水娃子都没影了她还看，就把她抱进了怀里，什么也不给看。何澜生鼻尖撞上了她的衣衫，闻了闻说：“好大一股醋味。”说完何无漪的脸都红了，她瞧着四周没人就凑上前亲了口，在她耳边低声说：“我是你媳妇，你是我的……嗯？”何澜生声音打了几个弯嗯了句，笑着不说话。")]),v._v(" "),_("p",[v._v("她还在等，等有一天无漪亲口说出来，就是等不到也不要紧。")])])}),[],!1,null,null,null);_.default=n.exports}}]);