(window.webpackJsonp=window.webpackJsonp||[]).push([[162],{636:function(v,_,p){"use strict";p.r(_);var t=p(2),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("如此一夜，何澜生睡得意兴阑珊，连着好几天没给何无漪好脸色。只是大冷天的没处去，屋里屋外的也就那么大点的地，也就不了了之的腻在一块去了。念心的病倒是好了，闷了几天瞅着年关将近就张罗起过年的东西来。")]),v._v(" "),_("p",[v._v("念心要忙起来，何澜生自然就退了掌勺的位子。以往和无漪两人的时候哪里会讲究什么，再说以前她也小做不得什么，两个人怎么舒心怎么来。这会念心倒是不赶何澜生出去了，拉着她在旁边要她好好学。何澜生也闲来无事，就拉着无漪一起在旁边帮忙。")]),v._v(" "),_("p",[v._v("临近年关，何澜生家厨房里的香味便一天盛过一天，馋得村里的小孩子总往这边来。念心喜欢孩子，总要拿些煎果蜜糖给他们吃。看着一个个吃的粘牙齿，念心就坐在那笑。“慢点慢点，别噎着了。”")]),v._v(" "),_("p",[v._v("夏雨见了，竟红着眼睛逃似的走了。")]),v._v(" "),_("p",[v._v("何澜生见她如此，本想去劝却给无漪拉住了，不由问：“你拉着我干嘛？”")]),v._v(" "),_("p",[v._v("“澜儿想去说什么？”何无漪低头问她，不忙不急的模样惹得何澜生瞪了她一眼。")]),v._v(" "),_("p",[v._v("能有什么，自然是劝说一番。毕竟念心等了夏雨那么多年，夏雨心里明白就不该继续蹉跎下去，难不成挨到老了才算情深意重一辈子么？何澜生心里虽是这样想的却也不说出来，反而去问无漪：“你以为呢？”")]),v._v(" "),_("p",[v._v("何无漪蹙眉认真想了会道：“不可不可。”")]),v._v(" "),_("p",[v._v("那模样似深思熟虑过了几遍，何澜生一时被她唬住了，以为两人有什么事情拉着她再问：“怎么不可了？”")]),v._v(" "),_("p",[v._v("“不好说……”")]),v._v(" "),_("p",[v._v("何澜生一噎，差点没缓过气来。但明知无漪这人不通情爱，还是听了她的话没追上去。或许….解铃还须系铃人，她心思一转就说：“念心，夏雨刚才喊你呢，像是急事。”")]),v._v(" "),_("p",[v._v("“她人呢？”念心一听急事忙问，也顾不上逗弄小孩子了。")]),v._v(" "),_("p",[v._v("何澜生指了指屋外，念心丝毫没多想就寻去了。何澜生咧嘴一笑，好不得意的去瞅何无漪，何无漪便依着她亲了一口，宠溺地道：“你啊……”")]),v._v(" "),_("p",[v._v("“我怎么了？”")]),v._v(" "),_("p",[v._v("“姐姐羞，羞羞羞。”屋里头不知谁家的小丫头瞪着水汪汪的眼睛一本正经都说，“这么大还要亲，还要抱，羞羞羞。”")]),v._v(" "),_("p",[v._v("有人领头其他小孩子也就起哄，纷纷对着何澜生吐舌头：“羞羞羞。”")]),v._v(" "),_("p",[v._v("何澜生瞪着那些在天天在家里蹭吃蹭喝的小萝卜头，脸红了。都是哪家的小孩子，通通送回家里去！何澜生冲上去一个个的捏脸，屋子里顿时闹成一团。")]),v._v(" "),_("p",[v._v("何无漪在一旁看着，也不上前。何澜生脸色很红，那红晕又随着笑声散开来，和一旁的小孩子混在一起，稚嫩而青涩。只是何澜生总也抓不住人，好似走一步都较那六七岁的孩子慢上一步似的，她却没觉得什么，依旧嘻嘻哈哈的随着孩子和她捉迷藏似的玩闹。")]),v._v(" "),_("p",[v._v("何无漪眨了眨眼睛，眸子里藏不住的湿意，脱口便唤了她：“澜儿过来。”")]),v._v(" "),_("p",[v._v("“不和你们玩了。”何澜生累得不轻，又拿了些吃食出来分了，“吃完早些回家，一群小鬼头。”")]),v._v(" "),_("p",[v._v("有东西吃时小孩子倒一个个乖得很，何澜生趁机挨个赏了个毛栗，这才笑了。不料还是之前那个小丫头摸着脑袋来了句：“姐姐才是小鬼头，羞羞羞。”说完撒开腿就跑了，小羊角辫还一翘一翘的，看的何澜生笑倒在无漪的怀里，余下的小孩也就跟着散了。")]),v._v(" "),_("p",[v._v("“澜儿，累不累？”何无漪见她都出汗了，掏帕子给她擦，也学着小孩子闹她：“没个样子，羞羞羞。”手却是环着她的腰，不让她再动了。")]),v._v(" "),_("p",[v._v("何澜生被说的恼羞成怒，磨着牙道：“羞什么羞，你再说我…”")]),v._v(" "),_("p",[v._v("“嗯？”何无漪歪头问，天不怕地不怕的样子真是气着了何澜生，何无漪却是又亲了她一口哄道：“不羞不羞。”")]),v._v(" "),_("p",[v._v("何澜生咬牙切齿，推了她作势要走，何无漪抱着她哪里肯松手，脸埋在她的脖颈间闷闷都说：“去哪里，娘想抱着你。”怕何澜生不许又亲了亲她，只把何澜生哄得没了脾气，由着她闹。")]),v._v(" "),_("p",[v._v("“我打算酿坛子酒。”")]),v._v(" "),_("p",[v._v("“澜儿一杯倒的酒量，还要喝酒？”")]),v._v(" "),_("p",[v._v("小瞧她，何澜生道：“屠苏酒益气温阳、祛风散寒，正是喝的时候。”")]),v._v(" "),_("p",[v._v("“孙师兄的方子？”何无漪想了想说，“屠苏酒味道太重，你定是不喜。”")]),v._v(" "),_("p",[v._v("何澜生一听就笑起来，敢情谁连药都不喝的，看着这埋在自己怀里人，妥协说：“你不喜欢喝，我改几位药材。”")]),v._v(" "),_("p",[v._v("“不喝药。”")]),v._v(" "),_("p",[v._v("这人，何澜生侧过头想咬她，见她嘟着脸的模样又心软了。“梅子酒呢？你没来之前我藏了一坛子。”说完就见人来了精神，瞅着她直要喝的馋嘴样。何澜生就笑，偏生不告诉她藏在哪里了。")]),v._v(" "),_("p",[v._v("一晃就到了除夕，村子里各家都忙了起来，烟囱更是一下午都没停过出气，饭菜的香味杂在一块，浓浓的都是年味。何家本是大家虽然何无漪这谷主不理琐事，念心该备的一点没差。从早上开始，她就喊了夏雨一起拾了木头在院子里架了火堆，何澜生觉得烟味大就扔了些香料进去，烧的满院子的香味。")]),v._v(" "),_("p",[v._v("念心忙着准备吃食，一整天手脚都没停下来过。何澜生写了桃符，做了幡子，本想去帮忙的，何无漪却拉了她说：“澜儿和我一起联句去。”")]),v._v(" "),_("p",[v._v("“你捡些平时喜欢的就好，念心还忙着呢。”")]),v._v(" "),_("p",[v._v("“我喊丘沙去。”")]),v._v(" "),_("p",[v._v("“你确定？”何澜生不相信地挑眉，丘沙可是大闲人，念心都敢使唤无漪了但看着丘沙闲得都不说一声，长得一张面瘫脸还真是省力气。正腹诽着丘沙就进来了，她依旧是一件红衣，除夕的日子脸上还是那般的清冷如霜：“何大夫，小主人。”特意还多看了何澜生一眼。")]),v._v(" "),_("p",[v._v("“晚上守岁可有喜欢吃的？念心在忙，你去看看。”何无漪说完，丘沙倒是没什么表示，点了点头应了。何澜生见她又是这样颇为头疼，无漪却一直拉着她走，只好说：“苏果给你裁了件冬衣，在念心那放着，明早记得换了…….”还没说完呢，无漪就拽了她进屋。何澜生有些不高兴：“话还没说完呢，你拉着我干嘛。”")]),v._v(" "),_("p",[v._v("“过后我与她谈。”\n“你们是不是有事瞒着我？”何澜生取了笔墨随口问，“怎么总见你们有事情谈？”\n“有些，我托她朋友打听南诏的事。”何无漪点头道，从箱子里找出一沓红纸，取了张铺开。何澜生上前拿了桌上的杯子镇纸道：“你还惦记着呢，都打听些什么了？”她说着比了比纸张的大小，又去拿了剪刀道：“我先裁出对子来，屋里屋外的都写？”")]),v._v(" "),_("p",[v._v("“都写。”何无漪应了声。")]),v._v(" "),_("p",[v._v("何澜生丈量好尺寸将纸折好，拿着剪刀就动手裁纸，她低着头认真的比对着，何无漪在旁帮忙，一时两人都无话。总共裁了六副，外加些其他的，何澜生数了数约么是够了，就放了纸去磨墨：“无漪想写什么？”")]),v._v(" "),_("p",[v._v("何无漪鼻尖蘸了墨汁，正欲提笔听何澜生这么一问还真停住了道：“澜儿呢？”")]),v._v(" "),_("p",[v._v("“我就是问问，又做不出什么绝妙文章来。”何澜生好笑地说，“你写写看，不喜欢咱们再换。往年也不见你动笔，今天怎么有心情了？”")]),v._v(" "),_("p",[v._v("“闲来无事。”何无漪道，见何澜生一直在笑就放下笔凑过去亲了口，“那和往年有所不同。”")]),v._v(" "),_("p",[v._v("何澜生微微仰头贴着她的脸颊亲了回来，可无漪搂着她就不松手了。何澜生无奈道：“还写不写了，墨都快干了。”")]),v._v(" "),_("p",[v._v("“我写上句，澜儿接下联。”")]),v._v(" "),_("p",[v._v("“除了大门口那副，其他随你。”")]),v._v(" "),_("p",[v._v("何无漪来了精神，提了笔就写：水滴月如天上月，月影沉浮。字迹大气的不像女子写的，笔力遒劲，似要破纸而出般，和习武以前比起来沉稳太多，咋一看她也忍不住赞了一句好来，回头却拿眼瞪了她，这么一比她的字就不用见人了。")]),v._v(" "),_("p",[v._v("何无漪就笑：“管那些作甚，澜儿接吧。”说着放下笔让出位置来，何澜生沉思半天也没啥感觉，她虽没正经读过四书五经诗三百，好歹肚子里也是有些墨水的，思来想去总算是有了：“眼中人是面前人，人心所归。”写完自己一看自觉还不错，也不多做纠结，换了下一张纸。")]),v._v(" "),_("p",[v._v("何无漪也挺喜欢的，拉着她再要写，如此闹了大半个时辰才算是写完，念心都开始上菜了。何无漪也不急，踩着凳子贴自己的对联。念心端菜进门看了一眼，见写的颇为不错笑着道：“谷主今年可是上了心，明早开门和少谷主一起去插幡子。”")]),v._v(" "),_("p",[v._v("“好。”何无漪也没推辞，念心见她好说话，感叹一句谷主成了亲总算有了个样子，心里却没觉得多好，只是大好的日子苦着脸不好，上了菜又匆匆回了厨房。她本来就是实在性子，不高兴如何也笑不出来，闷着脸一进厨房夏雨就说：“谁又惹着你了？除夕夜里高兴点。”")]),v._v(" "),_("p",[v._v("“咋啊，我还不能缓口气。”她对着夏雨就来了脾气，最近也不知道是咋了，夏雨总是一副欠了她几辈子的债没还似的，舔着脸和声和气的对她。念心心里莫名其妙，见了她总不免呛上几句，就是不自在。")]),v._v(" "),_("p",[v._v("“好了，五辛盘还要不要做，谷主少谷主都嫌那味重，今天做了好几个菜就不做了吧，也吃不完。”")]),v._v(" "),_("p",[v._v("“你不吃我还吃呢，都是老规矩了怎么能忘了。”又嘱咐道，“火烧大点。”她忘了烧火的是丘沙，见到在火光下映得诡异的半张脸，声音就弱了下去。夏雨见丘沙没听见便又说了声，念心才算是对她有了点好脸色。")]),v._v(" "),_("p",[v._v("一家人忙忙碌碌的到了天黑才上了桌，院子里的火堆烧得噼里啪啦的作响，何澜生也挖出了那坛梅子酒，上了桌何无漪也不爱说些什么，举了杯就喝。念心她们早就习惯了，碰了杯后各自开吃。何澜生颇为不满，安安静静地吃饭算什么守夜，拉着何无漪道：“咱们来行酒令，要不猜谜也行，谁输了要罚。”")]),v._v(" "),_("p",[v._v("念心立马就接道：“好，就这样玩，谁输了谁要喝酒。少谷主这梅子酒怎么酿的，快来教教我。”")]),v._v(" "),_("p",[v._v("“酿酒回头再说。我先打头，这律令就不要了，咱就轮一圈接，接的慢了可要罚三杯。”何澜生眨了眨眼睛，对着何无漪道：“打马川上走。”何无漪手里还执着酒杯，歪头就道：“行舟绿水前。”说完自己饮了一杯，舔了舔嘴角笑起来。")]),v._v(" "),_("p",[v._v("行舟绿水前，好像在哪里听过，何澜生一时没想起来，见无漪那谗样就去抢了她的酒杯，嗔道：“少喝点，醉了我可不管你。”哪知一会功夫念心夏雨丘沙三人都接了一遍，正好轮着她了。")]),v._v(" "),_("p",[v._v("“少谷主输了，快罚三杯。”念心抓了何澜生就笑着说，“谷主可不许替。”")]),v._v(" "),_("p",[v._v("何无漪偏偏拿了杯子斟满了道：“澜儿因我而输，我更要代她。”")]),v._v(" "),_("p",[v._v("“唉唉，不行不行，酒还是少谷主得喝。夏雨你说说。”念心喝了两杯也上脸，瞧着脸蛋红彤彤的，夏雨夹了些菜到她碗里道：“少谷主自己说。”")]),v._v(" "),_("p",[v._v("何澜生遂笑着说：“那我先喝了这三杯，念心你可不能说我耍赖了。”何澜生喝完一杯亮了亮杯底，只是喝的急了脑子有些晕，何无漪忙去抱了她放在腿上，“好了，莫要逞强。”")]),v._v(" "),_("p",[v._v("“我才不是一杯倒，无漪你小瞧我。”何澜生挥了挥手去够酒杯，想当初她也是纵横酒场的高手，区区一杯果酒算的了什么。何无漪见她胡闹，抓了她的手束在怀里，任何澜生怎么折腾都没用。")]),v._v(" "),_("p",[v._v("念心见了何澜生的醉态就笑：“少谷主方才还喊着要玩呢，这一杯就醉了。”")]),v._v(" "),_("p",[v._v("“你也少喝点，别醉了。”夏雨在一旁叮嘱。")]),v._v(" "),_("p",[v._v("何澜生只是头有些晕，见无漪这么束着她颇为不高兴，嘟囔道：“无漪我不喝了，你放我下来。”")]),v._v(" "),_("p",[v._v("“不放。”说着还抱紧了些，何澜生把脑袋靠在她肩膀上蹭了蹭：“饿……”说完何无漪就要喂她，何澜生还记着念心她们在呢，躲着不吃只是笑着看着何无漪的眼睛。两人你来我往旁若无人的亲昵起来，直把念心看得面红耳赤。")]),v._v(" "),_("p",[v._v("何澜生吃了半碗饭就睡了，村子里却是刚热闹起来，各家各户围着院子里的火堆载歌载舞，等着除夕夜的过去。那一夜何澜生睡得及其安稳，连一个梦都没有。")]),v._v(" "),_("p",[v._v("行舟绿水前：出自唐代王湾的《次北固山下》。王湾：唐玄宗先天年间（约 712 年）进士及第。")])])}),[],!1,null,null,null);_.default=n.exports}}]);