(window.webpackJsonp=window.webpackJsonp||[]).push([[169],{646:function(v,_,p){"use strict";p.r(_);var t=p(2),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("何无漪慢慢止住了眼泪，就是偏过头不去看何澜生。何澜生揉了揉饿瘪的肚子，桌上的桂花粥都没点热气，这大冷的天肯定是不能再喝了。一时没忍住，肚子又咕噜叫了一声，叫的何澜生都红了脸，身边何无漪还在生闷气一样不搭理人。何澜生还要去亲她，却被她躲得远远的，一点都不似方才泪眼朦胧乖乖躲在她怀里的小模样。")]),v._v(" "),_("p",[v._v("那样子的无漪啊，脆弱的让她心疼，那双眸子锁着她的身影，在灰暗的周遭却明亮的犹如星辰，何澜生犹如被蛊惑了般一次一次的亲吻她，含着眼角滑出的眼泪替她咽下去。她眼中那个清风朗月率性而为的何无漪，却不知何时起一而再再而三的哭泣。")]),v._v(" "),_("p",[v._v("都是她的错。何澜生心里自责却更是无力，往后还有多久呢。何澜生心口顿顿的疼了下，却放柔了声音说：“我饿了。”")]),v._v(" "),_("p",[v._v("“无漪，无漪……”何澜生喊了几声都没人应，正要起来却被何无漪一把抱住。")]),v._v(" "),_("p",[v._v("“无漪，我饿了。”")]),v._v(" "),_("p",[v._v("“想吃什么，我喊人给你做。”何无漪声音有些哑，像是慢慢回了神般摸了摸她的手脚，“外头冷，别受凉了。”说罢给何澜生盖好被子起身理了理褶皱的衣服，收拾一番又撑起些精神来。")]),v._v(" "),_("p",[v._v("“你生气了？”何澜生抿了抿唇，拉着她不让她走。")]),v._v(" "),_("p",[v._v("“生什么气，你是我的孩子还能有错。”何无漪说完轻笑了声，低眸凝视她道：“我不管那些，澜儿啊……”")]),v._v(" "),_("p",[v._v("何澜生心里泛酸，忍着泪应了声：“嗯？”")]),v._v(" "),_("p",[v._v("“一直陪着娘可好？”")]),v._v(" "),_("p",[v._v("何澜生低着头不答，以前她大可毫不顾忌地说出来，可现在她要如何应！")]),v._v(" "),_("p",[v._v("“澜儿，好不好？”何无漪揉着她的脑袋又问了句，还哑着的声音带着几分恳求，何澜生深呼了好几口气，到底没忍住呜咽出来：“好，我陪你，陪你一辈子。”她犹记得去年成婚的时候，多么想以后洗衣煮饭家长里短的就那么陪着她，照顾她……然而她心里明白，就算再怎么答应，她都无法挨到二十岁。然而她无法开口，对着何无漪半个字都说不得。")]),v._v(" "),_("p",[v._v("“乖。”何无漪点头，抚着她的背道：“我也饿了，陪我用些饭。”")]),v._v(" "),_("p",[v._v("何澜生眨了眨眼睛，眼中的泪落下来也不管，牵起嘴角应道：“好。”其他事情何澜生遂不再多说，仿佛什么都不知道一般。")]),v._v(" "),_("p",[v._v("何无漪低眸瞧着她的模样，直道她傻气，眼神里却是掩不住的难受。")]),v._v(" "),_("p",[v._v("何澜生仿若未觉，拉着何无漪一起连吃了两碗米饭，吃完就捂着肚子喊饱，赖在她的怀里不想动弹。许是之前睡得多了，何澜生挨了半天都没半分睡意，若不是这会夜了她还真不想就这么窝着。夜里太静，左右她又干不了什么事，只能数着无漪的心跳，像极了她以前连路也走不稳当的时候。")]),v._v(" "),_("p",[v._v("一眨眼的功夫，真是快啊。何澜生眼眶有些酸，忙阖了眼把脑袋往她怀里拱。一整晚都在胡思乱想着，也不知道何时睡过去的。再醒过来时，日头已经过了头顶。何无漪却也陪着她躺着，面上还挂着黑眼圈像是昨晚比她睡得还晚。何澜生心疼起来，也知道现在她这样子无漪哪里能睡得好，也没劝她，只是道：“还说我贪吃好睡，你看你……”")]),v._v(" "),_("p",[v._v("何无漪蹭了蹭枕头，还真有几番慵懒的摸样道：“都是澜儿带坏的。”说罢睁开眼来，神情还带着几分困倦。")]),v._v(" "),_("p",[v._v("“都说上梁不正下梁歪，哪有我带坏你的说法。”何无漪捏了捏她的脸，“不去看看晋阳？”")]),v._v(" "),_("p",[v._v("“一早去了。”何无漪应道，“长孙姑姑生养之时精血过度损耗，晋阳本就先天不足，这些年多思多虑，失于调养......”她叹了声，“我也不过拖得一时，还得看她自个能不能撑过去。”")]),v._v(" "),_("p",[v._v("何澜生脑子里搜刮了一番，也没想起来历史上晋阳到底活了多久，只得劝慰道：“会的，长孙皇后泉下有知也必然想她挺过去。”")]),v._v(" "),_("p",[v._v("何无漪颔首，起身取了衣服穿戴。何澜生见她起来，也没再磨蹭跟着起来洗漱。待两人到了店中大堂时，元芊居然陪着个女孩在大堂里坐着。何澜生打量了番女孩的面容，当下就确定是晋阳了。")]),v._v(" "),_("p",[v._v("元芊眼尖一眼就看到她们，问：“何大夫可还没用饭？”也不等无漪回就喊了婢女去备菜，晋阳见了何无漪也笑着喊：“何姑姑，小何姐。”她话里透着股亲昵，倒让想绕道走的何澜生停住了脚步，要说起来晋阳还长何澜生一辈，小公主为何如此？")]),v._v(" "),_("p",[v._v("何无漪点头应了声道：“出来坐坐也好。”说着与她一起坐了，搭了脉问道，“药可有按时吃？”")]),v._v(" "),_("p",[v._v("四人桌坐了三个，何澜生见此只好往元芊那坐下，看着无漪对着晋阳嘘寒问暖便是不喜，但元芊又在一旁似笑非笑的盯着她，何澜生只好装作无所事事的模样看着窗户。窗户被糊得严严实实，哪里看得见什么。")]),v._v(" "),_("p",[v._v("厨房的饭菜陆陆续续就开始上了，何澜生本就饿了见何无漪还在与晋阳说话就没了胃口，连何无漪辞行都没听见。元芊却是愣了下，低声问她：“你还要跟她走？”")]),v._v(" "),_("p",[v._v("何澜生不知她说什么事，却也没丝毫考虑的点头。\n元芊脸色凝重又追着问了句：“你恐怕真的会……为了她把命都搭了也不后悔？”她虽刻意压低了声音，哪里逃得过何无漪的耳朵，何无漪的耳尖动了动，也不说话了。晋阳眸子在三人中转了圈，定定地看了眼失态的元芊，也没再开口。")]),v._v(" "),_("p",[v._v("何澜生倒是没注意旁人如何，挑眉回道：“你别要把我当三岁小孩看。”几次三番，何澜生就算知道元芊说的是实话也实在生不出半点好感。")]),v._v(" "),_("p",[v._v("元芊像是受了什么打击一样，失落地摇头：“为什么不醒醒呢，一个个非要陷得这么深……”颇为怜悯的看了她一眼便起身走了，晋阳也忙追了上去。")]),v._v(" "),_("p",[v._v("元芊走的急，躲进了屋就急切的从袖袋里取出那漂亮的紫玉九连环，解了半天也没解开才泄气的把自己仍在了床上，想着何澜生那痴心无悔的模样忍不住笑，像是见了世间最可笑的事情。")]),v._v(" "),_("p",[v._v("晋阳在门外听了一会，本欲敲门的手最后还是垂了下去，低声叹了句：“这世间于你而言莫不皆是虚妄……”声音很轻，轻的在元芊的笑声中翻不出半点水花。")]),v._v(" "),_("p",[v._v("何澜生非常不喜欢元芊那可怜她的样子，一时蹙紧了眉头。可一抬眸见何无漪红了眼，心里的烦闷就去了大半，她早就不管以前的种种又想那些干什么，朝她傻笑了回说：“看着我做什么，快些吃饭。”说罢起了筷子用饭。")]),v._v(" "),_("p",[v._v("席间无声，何澜生不想碰那些烦心事，却也没憋住心里头的醋坛子，吃得差不多就问：“方才你们谈些什么？”")]),v._v(" "),_("p",[v._v("“晋阳的病还需慢慢来，急不得。”")]),v._v(" "),_("p",[v._v("何澜生一听晋阳就不高兴，晋阳晋阳说得晋阳才是她亲生的！方才看着无漪近乎宠溺的眼神对晋阳嘘寒问暖何澜生心里头就窝着火，扔下何无漪便要走。何无漪忙起身去拉她，衣角没拽住就赶上前一步从背后拥着她，何澜生呀了一声，顿时脸颊绯红，似如火烧。")]),v._v(" "),_("p",[v._v("何无漪轻笑起来，抱着她眨眼消失在大堂。")]),v._v(" "),_("p",[v._v("何澜生被她抱进了屋里才给松手，脸颊的红晕还没褪狠狠嗔了她一眼，大庭广众之下像什么样子。遂是不理她，喊了小厮再送些点心进来，省得闷在屋子里无聊。何无漪却是不知怎的挨着她又抱，闹得何澜生忙关了门，红着脸斥道：“何无漪！”也不知被人看见没，何澜生恨不得咬她一口。瞧她高兴的样子，不就是和她一起走嘛，走都走了这么些年，有什么高兴的！心里这样想，却是由着她抱了，连手也搭上她的腰间将她抱住，抱住了就舍不得松开。")]),v._v(" "),_("p",[v._v("“叩叩叩。”")]),v._v(" "),_("p",[v._v("“叩叩叩……”")]),v._v(" "),_("p",[v._v("敲门声响了两遍何澜生才松开了手，开门一看却不是送点心的小厮而是汝南公主。她还未开口汝南就越过她问何无漪：“阿漪你们要走？”")]),v._v(" "),_("p",[v._v("“此地过于吵闹，我约了师兄在城外相见。”")]),v._v(" "),_("p",[v._v("“城外地偏，不如随我去别苑，正好我也禀了父皇带晋阳去修养一阵子，这大冷的天你们哪里能走的。”说罢意有所指地看了眼何澜生。")]),v._v(" "),_("p",[v._v("“我…”何无漪似是顾忌什么，并没有答应下来，汝南见她如此也顾不得公主威仪，气得喊：“阿漪，我们相识二十多年你还不信我！”")]),v._v(" "),_("p",[v._v("何无漪遂不再多虑道：“好。”揉了揉不说话的何澜生，像是安慰她一般。何澜生不想闹的她们朋友不和，虽然很不想去也没拂了她的意思。")]),v._v(" "),_("p",[v._v("汝南公主也是个雷厉风行的主，说了走立马就备了马车要启程。何澜生只得匆匆收了包袱上车，这回路上的人多了不少，晋阳也跑来同她们一起。有旁人在何澜生只得规规矩矩地挨着何无漪坐了。")]),v._v(" "),_("p",[v._v("“这么自在？”何无漪见她如此规矩，不由问了句。揽过她的肩，捉了她的手放在手心里暖着，亲昵的让晋阳好生眼红。何澜生顺着她的意思靠着她，摇了摇头。")]),v._v(" "),_("p",[v._v("“何姑姑这样，可真是羡煞兕儿了。”晋阳笑着说，眼底不由流露出些难过来。")]),v._v(" "),_("p",[v._v("“晋阳也该大了。”何无漪点头说，却没松开何澜生。何澜生听得哭笑不得，人家才十来岁的孩子，早年丧母又还病着，哪有无漪这样说话的。何澜生回头见晋阳快哭了不由狠狠瞪了她一眼，想从她身上起来。可何无漪总喜欢抱着她，如何也不松手，又听晋阳道：“何姑姑说的是。”何澜生就松了力气乖乖的由她抱着了，闷声说：“她说的哪里是了。”")]),v._v(" "),_("p",[v._v("“小何姐为何一直躲着兕儿？”")]),v._v(" "),_("p",[v._v("何澜生不好意思红了脸，她方才胡乱吃了些飞醋而已。何无漪更是不留情面的戳破她：“澜儿小性子犯了，不碍事的。”")]),v._v(" "),_("p",[v._v("晋阳也笑：“以前小何姐不是这样的。”")]),v._v(" "),_("p",[v._v("“被我宠坏了。”何无漪抱着她说，脸上的笑容突然就没了。晋阳一愣，看了眼何澜生，竟不知她何时睡了过去，毫无声息的样子让她心里咯噔一下。再回头看何无漪的脸色，竟说不清的酸楚。")]),v._v(" "),_("p",[v._v("马车里一时安静极了。")])])}),[],!1,null,null,null);_.default=n.exports}}]);