(window.webpackJsonp=window.webpackJsonp||[]).push([[59],{536:function(v,_,p){"use strict";p.r(_);var t=p(2),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("且说正月初二那天遇上了大雪，念心和夏雨半路找了家猎户落脚，过了大半个月才见大雪化开。两人出了山也不见何无漪娘俩有个信来连丘沙都联系不上，好歹是问到了何无漪的去处，就马不停蹄的赶去了汝南县。")]),v._v(" "),_("p",[v._v("“夏雨，你说少谷主会不会出什么事？”念心在船里不住地往外看，半天只见江面白茫茫一片又追着问：“我这心里总是不踏实，怎么还没到。”")]),v._v(" "),_("p",[v._v("“你别瞎想，嘴上都长泡了还念叨。”夏雨脸色也是不好，她路上已经打听到汝南公主府上遇袭，也不知她们娘俩如何了。但见念心这么着急上火，她也不敢多说什么。")]),v._v(" "),_("p",[v._v("两人盼着盼着总算下了船，不带歇就赶到了汝南公主的别苑。只是到了也是侍女迎了她们进去，兜兜转转半天愣是没见着何无漪，可把念心急坏了，就差没抓着人上前问了。夏雨怕她心急起来乱说话，忙拉紧了她，一边也小声的和侍女套话：“姑娘可有见过我家谷主和少谷主，大半个月没见她们俩，心里头总是不踏实。”")]),v._v(" "),_("p",[v._v("“哪里是不踏实，天寒地冻的往外跑，谁省得了心。”念心清楚汝南公主与何无漪的交情，心里多少是不怕的。")]),v._v(" "),_("p",[v._v("“这就快到了，何大夫喜欢清静不喜人服侍，两位请。”侍女恭敬地说，半点不敢说何无漪的事情。")]),v._v(" "),_("p",[v._v("夏雨推了门进去，就见坐在门口的何无漪抬眸看她。那双眼睛隐隐像是哭过似的，才一个月不见她家那不想世事的谷主，恍然间已经苍老。")]),v._v(" "),_("p",[v._v("“谷主，谷主哎……”念心见她神情不振的模样心里一酸，赶忙跑了上前，只是近处一看才看清被她抱在怀中的何澜生，面无血色瘦得只剩一层皮。念心心针扎似的疼，打小看到大的两人怎么就成了这副模样。")]),v._v(" "),_("p",[v._v("“去歇会吧。”何无漪抱紧了何澜生道，“不碍事的，澜儿过些日子就会醒的。”她搂着何澜生的身子，躲着念心的触碰。夏雨见她这样，拉了把念心。念心哪里歇得下去，小声劝道：“谷主，门口风大，进去坐。”看见门口矮凳上摆着的都是少谷主爱吃的，她险些落下泪来。只得拿袖子猛地擦了擦，跟着何无漪进屋。")]),v._v(" "),_("p",[v._v("屋里头却比外头更觉得冷来，门口还有点光亮，这里头都没盏灯亮着，黑乎乎的没半点人气。念心忙把炭炉生了起来，又把灯都点上了身上才有了些暖意。念心虽怨何无漪是个不会照顾人的，见她这般哪里还顾得上说她。“谷主用了饭没？”")]),v._v(" "),_("p",[v._v("“用了些。”")]),v._v(" "),_("p",[v._v("念心哪里放心，收拾完屋子又下厨房去了。夏雨也不拦着她，将屋内的窗户都打开了透气，只是半天也不见何无漪说句话，她几番欲言又止倒是引得何无漪开了口：“我无碍，只是有些久了，每天惶惶然不知该如何是好。”")]),v._v(" "),_("p",[v._v("“你听我一句，先顾好自个。少谷主会好起来的。”")]),v._v(" "),_("p",[v._v("“是啊，澜儿会醒的，会醒的。”何无漪喃喃地念着，似是中了魔障般。夏雨私下去找孙思邈，得知何澜生就掉着那一口气在，好几夜都没合眼。“少谷主没了，谷主该怎么活啊，哎…”老夫人的事在前，她更不敢和念心说半句，只得时时刻刻守着何无漪生怕她有个三长两短，把头发都愁白了。")]),v._v(" "),_("p",[v._v("转眼又过了大半个月，何澜生的脸色越发差了，汝南公主府里不知何时传出些谣言来，说的都是些人死也不入土为安有违天理之类的。汝南狠狠整治过一番，但何无漪却不管，抱着越来越轻的何澜生半刻都不松手。便是汝南邀了她出门踏青，她也要抱何澜生同去。")]),v._v(" "),_("p",[v._v("念心哪里肯，拦着道：“路上磕着哪怎么办，万万不行。”")]),v._v(" "),_("p",[v._v("别说念心，其他人也是不同意。何无漪当下就不想去了，念心又道：“谷主你出去走走也好，待着屋子里快发霉了。去吧去吧。”不由分说将何无漪推出了门。那厢晋阳又在拉她：“何姑姑，山上的玉兰花开的极盛，不如去摘些回来，何姐姐肯定喜欢。”")]),v._v(" "),_("p",[v._v("何无漪眼睛一亮，就跟了她们同去。只是出了门她就心神不宁，跟着她们走了一路也没半分喜色，直到瞧见了凌寒而绽的玉兰，就顿住了。眼前好似又见了那个在树下仰头嘟囔没找到花苞在哪的身影，她失神地上前，却扑了个空。")]),v._v(" "),_("p",[v._v("一时回过神来，失魂落魄险些跌倒在地。")]),v._v(" "),_("p",[v._v("汝南在一旁瞧得心疼，想去扶她，却被何无漪拦住了：“我无碍。”她眼眶泛红，强撑着朝汝南道：“澜儿她，澜儿她也喜欢，我…”她抬头，眼神越过汝南晋阳，越过那一群群的侍从侍卫，望向远处，山风呼啸天地浩荡…")]),v._v(" "),_("p",[v._v("突然之间她就变了脸色，折了枝玉兰花眨眼就没了人影。汝南无奈由她去了，两个月里该劝的都劝了，她也死了心。")]),v._v(" "),_("p",[v._v("踏青的行人只觉面前一阵风刮过，带着玉兰的幽香远去，及至汝南公主府门前，才瞧得见气息不定的何无漪。她满身是汗，鬓间的头发散乱地贴着脸颊，但那双眼睛却亮得惊人，似星空皓月，碧海明珠。")]),v._v(" "),_("p",[v._v("公主府的侍卫想要上前，却被她挥手拦了下来，她说：“停。”")]),v._v(" "),_("p",[v._v("话落的一刹那，风止云歇，所有人都被定在了那一刻，独独何无漪一人推开了公主府的大门，她迈开步子，穿过长廊，绕过假山，直到北苑落星居前，看着睡着的何澜生睁开了眼睛。")]),v._v(" "),_("p",[v._v("何无漪望着她，望着她落下泪来。身后静默的世界，仿佛雪落花开，消无声息间复归于原位。")]),v._v(" "),_("p",[v._v("何无漪听着眼前的人喊她：“无漪”，却倔强地站着，手中的玉兰几乎被她捏碎都没动。可她不动何澜生就撑着手下了地，摇摇晃晃地，一步一步挪到了她的面前，抱着她，唤着她。“无漪。”")]),v._v(" "),_("p",[v._v("何无漪深深地吸了口气，逮着她就咬了一口，那一口用了狠劲把何澜生的脖子都咬出了血丝。何澜生痛得直吸气，何无漪却把她抱地更紧：“疼不疼？”")]),v._v(" "),_("p",[v._v("“疼。”")]),v._v(" "),_("p",[v._v("何无漪的眼泪就落了下来：“傻澜儿，傻澜儿。”那双眸子里的悲与喜，太多太多。")]),v._v(" "),_("p",[v._v("何澜生以为无漪会欢喜于自己醒来，可眼下她却是痛苦的。何澜生贴着她蹭了蹭道：“你哭什么，我醒了不高兴？”")]),v._v(" "),_("p",[v._v("“高兴。”何无漪点头，抹了把眼泪忙不迭地点头，“娘高兴。”可神色中掩不住的心疼又哪里说得上是高兴。何澜生更是委屈，扭了头不去理她。她不清楚无漪是不是知道了什么，或者无漪又曾经是谁，可她都走到这一步了又哪会去管那么多。她不愿见无漪这般，甚至再往深了想，她又不确定这人到底…到底是否爱过她。")]),v._v(" "),_("p",[v._v("然而何澜生已经无力再去思虑那些，她只想见这人，仅此而已。可何无漪这般令她心里没由来的恼怒，恼怒这人咬了她一口就难过去了，真是岂有此理。“何无漪，你要是难过我就闭眼再睡过去了！”见那人怔怔地看着她，何澜生挫败地问：“你是不是不想我醒过来？”梦境还是现实，无漪是不是也曾分的很清楚。")]),v._v(" "),_("p",[v._v("何无漪猛然就抱紧了她，禁锢地何澜生动都动不了。“没，”何无漪急地地道，“娘做梦都想着澜儿醒！”")]),v._v(" "),_("p",[v._v("这般坦诚的话听得何澜生顿时喜上眉梢，蹭着她的脸颊亲了口。")]),v._v(" "),_("p",[v._v("何无漪揉着她的脑袋，迷茫地道：“想的时候多了，总也分不清楚何处是何处。澜儿，澜儿，真的是你？”她贴着她的脸颊，两人如鸳鸯交颈，缠得越发难解难分。")]),v._v(" "),_("p",[v._v("“不是我还会是谁，以前总说我胡思乱想，你看你还能把我认错了。”何澜生低声嘀咕，若不去看她那微红的眼睛还真以为她在埋怨何无漪了。")]),v._v(" "),_("p",[v._v("“我…”何无漪拉长了声音，又突然歇了，她蹙起眉看着何澜生，像是难以开口。")]),v._v(" "),_("p",[v._v("何澜生却像是没看见一般道：“我渴了。”")]),v._v(" "),_("p",[v._v("“等一会。”何无漪起身去倒水，回来就见何澜生和衣又躺了下去。何无漪忙拉住她问：“困了？”她问地急切，双手已经搭上了何澜生的脉。")]),v._v(" "),_("p",[v._v("何澜生握住她发凉的手心道：“坐着冷，腿脚也没力气我就想躺一会。”刚说完何无漪就把杯子放在一边，掀了被褥。何澜生忙缩了脚回去，按住她的手道：“无漪，我回来了。”")]),v._v(" "),_("p",[v._v("何无漪低着头坐在那，良久才道：“澜儿会醒的，我晓得。”她的双肩颤抖地厉害，手却死死的拽住何澜生的手，像是怕极了。")]),v._v(" "),_("p",[v._v("何澜生抽了抽发酸的鼻子，倾身将她拥进了怀里，用女人最为柔软的地方贴着她，好让她心安。“无漪，我本就属于这里，又会到哪里去。”")]),v._v(" "),_("p",[v._v("“我之前说的都是气话，怪你是不是顾着我的病应下我。”何澜生见她乖乖地在自己怀里，心里也软成了一片，“我是气昏了头，当时又病得厉害，免不了耍些性子，你怎么就当真了？”")]),v._v(" "),_("p",[v._v("何无漪偏过脑袋不说话。")]),v._v(" "),_("p",[v._v("何澜生拍着她的背轻笑：“我是你生的还能反了你不成，再说嫁鸡随鸡嫁狗随狗，除非你不要我。”")]),v._v(" "),_("p",[v._v("“澜儿！”何无漪恼怒地道，“胡说八道，娘怎么会不要你！”她说完脸上又浮现出一股悲戚之色，不忍道：“娘被人所害才困于此，而今你…”")]),v._v(" "),_("p",[v._v("“你在哪我就在哪。”何澜生说完又不免生出几分怨气来，“我的心思你还是不懂么？”")]),v._v(" "),_("p",[v._v("“懂。”何无漪点头应了，又道：“只是舍不得。”")]),v._v(" "),_("p",[v._v("“有什么舍不得的，早十几年我就放下了的东西。”何澜生笑了笑，“我不在乎的，当年你可是说了哪也不许去的。”回想起来，当初的无漪还真是霸道的可以，那份不通世故的率直迷得她丢了魂，说什么都依着她了。")]),v._v(" "),_("p",[v._v("如今啊……何澜生心疼地去亲吻她的鬓发：“我听你的话了，怎么不见你夸我？”")]),v._v(" "),_("p",[v._v("许久何无漪都没抬头，何澜生只觉得胸口的衣衫湿透了一片，那滚烫的眼泪落在她的心口，疼，疼得如刀割一般。")]),v._v(" "),_("p",[v._v("何澜生醒了的消息很快就传了出去，孙思邈一听到消息便匆匆赶来，只是进屋一看何无漪还抱着何澜生，不由怒道：“小师侄醒了还一直抱着像什么样子，快快放下让为兄看看。”")]),v._v(" "),_("p",[v._v("何无漪眼眶还微微泛着红，听到孙思邈的话头也不抬地道：“澜儿已经无碍，师兄可以歇歇了。”")]),v._v(" "),_("p",[v._v("孙思邈登时吹胡子瞪眼，气得不轻。")]),v._v(" "),_("p",[v._v("何澜生见着不对劲赶紧出声，扯了扯何无漪的袖子道：“无漪，师伯看看也好。”")]),v._v(" "),_("p",[v._v("何无漪却是一点都不让，护着何澜生说：“不许。”那模样像是孙思邈要和她抢人似的，气得他长袖一甩道：“岂有此理！”说罢不再理会何无漪。")]),v._v(" "),_("p",[v._v("“师伯您还不知道无漪就这脾气，别气着身子。”何澜生赶紧劝，心里多少明白无漪不想让孙思邈与她诊治的缘由，只是心里难免疑惑，无漪是记起了什么还是元芊找她说了？看着方才在她怀里哭成一团的人，何澜生忍不住又亲了亲她。")]),v._v(" "),_("p",[v._v("“咳咳！”孙思邈咳了两声，他可没到老眼昏花的地步！没好气地瞪了她们两眼，索性不坐了：“醒了多出来走动，莫再抱着。”至于她俩那些说不得的事，孙思邈提来心堵甩袖子就走了。")]),v._v(" "),_("p",[v._v("何澜生亲完才知道过了，红了脸急忙要留住孙思邈，何无漪则拦着她自己去了。孙思邈见她跟上来免不了拿脸色给她：“你也不小了，再这么下去，往后如何是好？！”")]),v._v(" "),_("p",[v._v("“我守着澜儿。”何无漪点头说。")]),v._v(" "),_("p",[v._v("“你是她娘亲，往后要这样过下去？”孙思邈忧心忡忡地问，她们这样总归不是长处。")]),v._v(" "),_("p",[v._v("“是。”何无漪并无躲闪，而是认真地回道，“往后我心里有数，师兄挂心了。”")]),v._v(" "),_("p",[v._v("孙思邈见她行事不算糊涂，那些规劝的心思也就罢了。仍不放心地叮嘱一番：“我老了，不懂你们心里头在想什么。孰轻孰重你自个清楚便好。小师侄年纪也不小了，既然定了就定下心来，莫去想些有的没的。”")]),v._v(" "),_("p",[v._v("“是了。”")]),v._v(" "),_("p",[v._v("“回去吧，小师侄刚醒必然离不得你。”孙思邈摆手让她回去，何无漪一路送他出了北苑才原路返回。刚到门口见何澜生靠在门上等她，搂了她问：“怎么出来了？”")]),v._v(" "),_("p",[v._v("何澜生歪了歪头道：“想着见你。”好像一刻都离不得，何澜生手心微微沁出了些汗，又满足地笑开来。")]),v._v(" "),_("p",[v._v("还能见她，真好。")])])}),[],!1,null,null,null);_.default=n.exports}}]);